import axios from "axios";

export const uploadToCloudinary = async (file) => {
  const formData = new FormData();
  formData.append("file", file);
  formData.append("upload_preset", "Notes App"); // Replace with your preset

  try {
    const response = await axios.post(
      `https://api.cloudinary.com/v1_1/dtipbsdyx/upload`, // Replace with your Cloud Name
      formData
    );
    return response.data.secure_url; // Returns the uploaded file URL
  } catch (error) {
    console.error("Upload failed:", error);
    return null;
  }
};
